company_total = []
for _ in arial:
    company_total.append(_['domain'].split('.')[0])
company_description = []
company_speicalities = []
company_total = set(company_total)
company_total = list(company_total)

def get_specialities(soup):
    try:
        specialities = soup.body.find_all('p', {
            'class': u'org-about-company-module__specialities mb5 Sans-15px-black-70% mb3', })
        if specialities:
            for i in specialities:  # although there are only one element in specialities
                S = re.search(r'\n(.+?)\n', i.text)
                company_speicalities.append(S.group(1))
                return S.group(1)
    except:
        specialities = soup.body.find_all('p', {
            'class': u'sans-14px-black-75 about__detail-text show-more-only', })
        if specialities:
            for i in specialities:  # although there are only one element in specialities
                S = re.search(r'\n(.+?)\n', i.text)
                company_speicalities.append(S.group(1))
                return S.group(1)


def get_company_description(soup):
    description = soup.find_all('p',{'class':u'org-about-us-organization-description__text description mb5 pre-wrap Sans-15px-black-70%'})

    if description:
        for i in description:  # although there are only one element in specialities
            company_description.append(i.text)



